var selectedLocations = [];
var date = new Date();
var locationcount = 0;
var locationname = "";

$(document).ready(function() {
    if($.cookie('username') === null || $.cookie('username') === "" || $.cookie('username') === "null" || $.cookie('username') === undefined){
        $('#login').modal({
            backdrop: 'static',
            keyboard: false,
            show: true
        });
    }else{
        loadFormTagesprogramme();
    }
    $('#logout').click(function(){
        $.removeCookie('username');
        $.removeCookie('password');
        $('#login').modal({
            backdrop: 'static',
            keyboard: false,
            show: true
        });
    });
    $('#loginButtom').click(function(){
        $.cookie('username', $('#username').val());
        $.cookie('password', $('#password').val());
        $.ajax({
            url: '/api/v2/playback/locations/',
            type: 'GET',
            dataType: 'json',
            success: function(data) {
                $('#login').hide();
                loadFormTagesprogramme();
            },
            error: function() { alert('Login incorrect or API Error! Try again.'); },
            beforeSend: setHeader,
            async: false
        });
    });
});

function loadFormTagesprogramme(){
    console.log('Loading sites');
    $.ajax({
        url: '/api/v2/playback/sites?max=1000',
        type: 'GET',
        dataType: 'json',
        success: function(data) {
            loadlocations(data);
        },
        error: function() { alert('Error while loading data from API!'); },
        beforeSend: setHeader,
        async: false
    });
    $('#select-locations').multiselect({
        enableClickableOptGroups: true,
        nonSelectedText: 'Select locations',
        buttonWidth: '235',
        onChange: function(element, checked) {
            var locations = $('#select-locations option:selected');
            selectedLocations = [];
            $(locations).each(function(index, location){
                selectedLocations.push([$(this).val()]);
            });
        }
    });
    $('#select-locations').multiselect('dataprovider', optgroups);
    $('#date').datepicker({
        todayBtn: "linked",
        format: "yyyy-mm-dd",
        todayHighlight: true
    });
    $('#date').datepicker()
        .on('changeDate', function(ev){
            date = ev.date;
        });

    $('#go').click(function() {
        $.each(selectedLocations, function(i, location) {
            docDefinition.content = [];
            docDefinition.header = {text: moment(date).locale("de").format("dddd, D.M.YYYY"), alignment: "center"}
            filename = createPage(location)+".pdf";
 //           if(selectedLocations.length > (i+1)){
 //           docDefinition.content.push(
 //               {
 //                   text: '.',
 //                   style: 'small',
 //                   pageBreak: 'after'
 //               });
 //           }
            pdfMake.createPdf(docDefinition).download(filename);
        });
    });
}

function createPage(location){

    var locationname = "";
    $.ajax({
        url: '/api/v2/playback/locations/'+location,
        type: 'GET',
        dataType: 'json',
        success: function(data) {
            console.log('Loading location data');
            locationname = data.name;
            console.log(data);
            },
        error: function() { alert('Error while loading data from API!'); },
        beforeSend: setHeader,
        async: false
    });
    docDefinition.content.push({
            columns: [
                {
                    width: 'auto',
                    stack: [
                        {
                        image: 'data:image/png;base64,',
                        height: 80,
                        width: 50
                        }
                    ]
                },
                {
                    width: '*',
                    alignment: 'center',
                    stack: [
                        {
                            style: 'h1',
                            text: locationname+' - ' +moment(date).locale("de").format("dddd, D.M.YYYY"),
                        },
                        {
                            style: 'h1',
                            text: '65. Berlinale',
                        },
                        {
                            style: 'h2',
                            text: 'Digital Cinema Tagesprogramm  '
                        }
                    ]
                }
            ]
   });


    docDefinition.content.push(
        {
            columns: [
                {
                    alignment: 'center',
                    width: '*',
                    stack: [
                        {
                            text: 'Checkliste für ' + moment((date.getTime() * 1 + 86400000)).locale("de").format("dddd, D.M.YYYY"),
                            style: 'subheader'
                        }

                    ]
                }
            ]},{
        text: 'Zwischen 7:00 und 10:00 Uhr bitte - ggf. gemeinsam mit der Saalbetreuung - alle Filme auf der Checkliste anspielen und dabei inbesondere CPL, KDM, Untertitel, Format und Ton prüfen. Bitte hakt dazu jeden Checkpunkt ab und informiert das Film Office unter Angabe der TimingNr über eventuelle Abweichungen.',
            style: 'medium'
    },
    {
        text: 'Bei Problemen während der Tests bitte sofort das Film Office unter der Notrufnummer anrufen!\n',
            style: 'medium'
    }
        );

    $('#progress').html('Creating Checklist for '+locationname+' ...');
    createChecklist(location);


    docDefinition.content.push(
        {
            columns: [
                {
                    alignment: 'center',
                    width: '*',
                    stack: [
                        {
                            text: '\nTagesprogramm '+moment(date).locale("de").format("dddd, D.M.YYYY"),
                            style: 'header',
                            pageBreak: 'before'
                        }

                    ]
                }
            ]}
    );

    $('#progress').html('Creating Schedule for '+locationname+' ...');
    createSchedule(location);
    return locationname + "_" +  moment(date).locale("de").format("D_M_YYYY");
}


function createChecklist(location){
    $.ajax({
        url: '/api/v2/playback/events/?locationId='+location+'&order=startTime&startAfter='+(date.getTime() * 1 + 86400000) +'&startBefore='+(date.getTime() * 1 + 172800000),
        type: 'GET',
        dataType: 'json',
        success: function(data) {
            console.log('Loading event data for location '+location + ' calling:'+ this.url);
            $.each(data, function(j, event) {
                var startTime = new Date(event.startTime);
                var eventItemTable = [];
                $.each(event.eventItems, function(k, eventItem) {
                    console.log(eventItem);
                    var mediaClipTable = [];
                    if(eventItem.movieVersionId != null){
                        // load mediaclip information
                        $.each(eventItem.mediaClips, function(m, mediaClip) {
                            $.ajax({
                                url: '/api/v2/mediaclips/clips/'+mediaClip.mediaClipId,
                                type: 'GET',
                                dataType: 'json',
                                success: function(data) {
                                    console.log(data);
                                    console.log('Loading package data for mediaclip '+mediaClip.mediaClipId);
                                    pklText = [];
                                    $.each(data.moviePackageIds, function(p, moviePackageId) {
                                        $.ajax({
                                            url: '/api/v2/packages/packages/'+moviePackageId,
                                            type: 'GET',
                                            dataType: 'json',
                                            success: function(packageData) {
                                                console.log(packageData);
                                                pklText.push({text: 'UUID: '+  packageData.externalId + ' Größe: ' + Math.round(packageData.size/1000/1000/10)/100 + ' GB'});
                                            },
                                            error: function() { alert('Error while loading package data from API! '+packageId) },
                                            beforeSend: setHeader,
                                            async: false
                                        });
                                    });
                                    console.log('Loading mediaclip data for '+mediaClip.mediaClipId);
                                    mediaClipTable.push({
                                        style: 'tableChecklist',
                                        table: {
                                            widths: [ '100%' ],
                                            body: [[
                                                [
                                                    {text: 'CPL: '+ data.contentTitleText + '[  ]'},
                                                    {columns: [
                                                        {
                                                            stack: [
                                                                'UUID:',
                                                                'Länge:',
                                                                'Typ:',
                                                                'ContentTyp:',
                                                                '3D:'
                                                            ],
                                                            fontSize: 8,
                                                            width: '15%',
                                                            bold: true
                                                        },
                                                        {
                                                            stack: [
                                                                data.externalId.toString(),
                                                                data.length.toString(),
                                                                data.type.toString(),
                                                                data.data.DCP.ContentKind.toString(),
                                                                janein(data.stereoscopic.toString())
                                                            ],
                                                            fontSize: 8,
                                                            width: 'auto'
                                                        },
                                                        {
                                                            stack: [
                                                                '[  ]',
                                                                ' ',
                                                                ' ',
                                                                '[  ]'
                                                            ],
                                                            fontSize: 8,
                                                            width: '5%',
                                                            bold: true
                                                        },
                                                        {
                                                            stack: [
                                                                'Leinwand-Format:',
                                                                'Container-Format:',
                                                                'Verschlüsselt:',
                                                                'FPS:'
                                                            ],
                                                            fontSize: 8,
                                                            width: 'auto',
                                                            bold: true
                                                        },
                                                        {
                                                            stack: [
                                                                data.screenAspect.toString(),
                                                                data.mediaAspect.toString(),
                                                                janein(data.encrypted),
                                                                data.fps.toString(),
                                                            ],
                                                            fontSize: 8,
                                                            width: 'auto'
                                                        },
                                                        {
                                                            stack: [
                                                                '[  ]',
                                                                '[  ]',
                                                                '[  ]',
                                                                ' '
                                                            ],
                                                            fontSize: 8,
                                                            width: '5%',
                                                            bold: true
                                                        }
                                                    ]},{text: 'Benötigte PKLs:'},pklText,
                                                ]
                                            ],[{text: 'Bemerkungen zur CPL: ' +remarks(data.screeningRemarks) , fontSize: 10}]]
                                        }
                                    });
                                },
                                error: function() {mediaClipTable.push({text: "Error: MediaClip not found in FMS. Film Office kontaktieren."});},
                                beforeSend: setHeader,
                                async: false
                            });
                        });
                    }else{
                        mediaClipTable.push({text: "Achtung: Für diesen Programmpunkt wurde noch keine Version hinterlegt. Film Office kontaktieren."});
                    }
                    eventItemTable.push(
                        {
                            table: {
                                widths: [ '100%' ],
                                body: [[
                                    [
                                        {text: 'Check für FilmNr. '+ eventItem.movieExternalId + ': '+event.name},
                                        {columns: [
                                            {
                                                stack: [
                                                    'Sprachen:',
                                                    'Bes. Technik:'
                                                ],
                                                fontSize: 10,
                                                width: '20%'
                                            },
                                            {
                                                stack: [
                                                    listLanguages(eventItem.audioLanguageIds)+ ' [  ]',
                                                    listLanguages(eventItem.tags)+ ' [  ]'
                                                ],
                                                fontSize: 10,
                                                width: '30%'
                                            },
                                            {
                                                stack: [
                                                    'Untertitel:'
                                                ],
                                                fontSize: 10,
                                                width: '20%'
                                            },
                                            {
                                                stack: [
                                                    listLanguages(eventItem.subtitleLanguageIds)+ ' [  ]'
                                                ],
                                                fontSize: 10,
                                                width: '30%'
                                            }
                                        ]}
                                    ]
                                ],[mediaClipTable]]
                            }
                        });
                });
                docDefinition.content.push({
                        text: 'TimingNr. '+event.externalId,
                        style: 'smallheader'
                    },
                    eventItemTable)
            });
        },
        error: function() { alert('Error while loading data from API!'); },
        beforeSend: setHeader,
        async: false
    });
}

