function loadlocations(data) {
    $.each(data, function(i, item) {
        console.log('Loading locations for site ' + item.name);
        $.ajax({
            url: '/api/v2/playback/locations/?max=1000&siteId='+item.id,
            type: 'GET',
            dataType: 'json',
            success: function(data) {
                children  = [];
                $.each(data, function(j, location) {
                    children.push( {label: location.name, value: location.id});
                    locationcount = j;
                });
                optgroups.push({label: item.name, children: children});
            },
            error: function() { alert('Error while loading data from API!'); },
            beforeSend: setHeader,
            async: false
        });
    });
}

function setHeader(xhr) {
    xhr.setRequestHeader('x-fms-auth-username', $.cookie('username'));
    xhr.setRequestHeader('x-fms-auth-password', $.cookie('password'));
}

function janein(bool) {
    if(bool == true){
        return "ja";
    }else if(bool == false){
        return "nein";
    }else{
        return "unbekannt";
    }
}

function remarks(string){
    if(string == null || string == "null" || string == "" || string == " "){
        return "keine";
    }else{
        return string;
    }
}

function listLanguages(object){
    languages = '';
    $.each(object, function(j, language) {
        languages = languages + language + ' ';
    });
    if(object.length == 0){
        languages = "keine";
    }
    return languages
}

